mapper.select:
SELECT r.route_id, r.user_id, rd.sequence, rd.content_id, rd.description
FROM route r
LEFT JOIN route_detail rd ON r.route_id = rd.route_id
WHERE r.user_id = #{userId}
ORDER BY r.route_id, rd.sequence;

select content_id, sido_code, first_image, title, addr1, latitude, longitude
from attraction_info
where content_id = #{content_id}
